import React, { useState, useEffect } from 'react';
import GlobalContext from './Components/GlobalContext';
import EmployeeList from './Components/EmployeeCrud/EmployeeList';
import AddEmployee from './Components/EmployeeCrud/AddEmployee';
import DeleteEmployee from './Components/EmployeeCrud/DeleteEmployee';
import UpdateEmployee from './Components/EmployeeCrud/UpdateEmployee';
import ViewEmployee from './Components/EmployeeCrud/ViewEmployee';


const EmployeeContainer = () => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [employees, setEmployees] = useState([]);
  // const [entities, setEntities] = useState([]);
  const handleEntityAdded = (newEntity) => {
    setEmployees([...employees, newEntity]);
  };

  const handleEntityDeleted = (employeeId) => {
    const idToDelete = parseInt(employeeId);
    const updatedEmployees = employees.filter((employee) => employee.id !== idToDelete);
    // Update the state with the new employees array (without the deleted employee)
    setEmployees(updatedEmployees);
  };

  const handleEntityUpdated = (updatedEntity) => {
    const updatedEmployees = employees.map((employee) =>
      employee.id === updatedEntity.id ? updatedEntity : employee
    );
    setEmployees(updatedEmployees);
  };


  useEffect(() => {
    fetch(GlobalContext.ApiUrl, {
      method: 'GET',
      headers: GlobalContext.headers,
    })
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setEmployees(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (<div className='wrapper'><EmployeeList employees={employees} />
      <AddEmployee onEntityAdded={handleEntityAdded}></AddEmployee>
      *****************
      <UpdateEmployee onEntityUpdated={handleEntityUpdated}   > </UpdateEmployee>
      *****************
      <ViewEmployee></ViewEmployee>
      *****************
      <DeleteEmployee onEntityDeleted={handleEntityDeleted}></DeleteEmployee>
      *****************
    </div>)

      ;
  }
};

export default EmployeeContainer;
