import React, { Component, component } from 'react'

export class Click extends Component {

    constructor(props) {

        super(props)

        this.state = {

            count: 0
        }

    }


    UpdateClick = () => {

        this.setState({ mycount: this.state.count + 1 })

    }

    render() {

        const { mycount } = this.state;

        return (

            <div>

                <button onClick={this.UpdateClick}>  clicked {mycount} time</button>

            </div>

        )
    }


}

export default Click
