import React, { useState } from 'react';
import DOMPurify from 'dompurify';
import GlobalContext from '../../Components/GlobalContext';

const AddEmployee = ({ onEntityAdded }) => {
  const [name, setName] = useState('');
  const [job, setJob] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!name || !job) {
      return;
    }

    const employee = {
      //try something like <img src=x onerror="alert(\'XSS attack!\');">
      name: DOMPurify.sanitize(name),
      job: DOMPurify.sanitize(job),
    };

    fetch(GlobalContext.ApiUrl, {
      method: 'POST',
      headers: GlobalContext.headers,
      body: JSON.stringify(employee),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('New employee ID:', data);
        // Reset the form fields
        setName('');
        setJob('');
        setSuccessMessage('New employee ID:' + data);

        onEntityAdded({ id: data, name: employee.name, job: employee.job });
      });
  };

  return (
    <div>
      <h2>Add Employee</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(event) => setName(event.target.value)}
            />
          </label>
        </div>
        <div>
          <label>
            Job:
            <input
              type="text"
              value={job}
              onChange={(event) => setJob(event.target.value)}
            />
          </label>
        </div>
        <button type="submit">Add Employee</button>
      </form>
      {successMessage ? <h2>New employee added: {successMessage}</h2> : null}
    </div>
  );
};

export default AddEmployee;
