import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchData } from '../../actions/projectActions';

const ProjectContainer = ({ data, loading, error, fetchData }) => {


    useEffect(() => {
        // Dispatch the fetchData action when the component mounts
        //debugger;
        fetchData();
    }, [fetchData]);

    debugger;
    if (!data || !data.projects || data.projects.length === 0) {
        return <p>No projects found.</p>; // Display a message when projects is null or empty
    }


    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <table style={{ borderCollapse: 'collapse', width: '100%' }}>
                <thead>
                    <tr>
                        <th style={tableHeaderStyle}>ID</th>
                        <th style={tableHeaderStyle}>Name</th>
                        <th style={tableHeaderStyle}>Client</th>
                        <th style={tableHeaderStyle}>Description</th>
                        <th style={tableHeaderStyle}>Start Date</th>
                        <th style={tableHeaderStyle}>End Date</th>
                        <th style={tableHeaderStyle}>Status</th>
                        <th style={tableHeaderStyle}>Team Members</th>
                    </tr>
                </thead>
                <tbody>
                    {data.projects.map((project) => (
                        <tr key={project.id}>
                            <td style={tableCellStyle}>{project.id}</td>
                            <td style={tableCellStyle}>{project.name}</td>
                            <td style={tableCellStyle}>{project.client}</td>
                            <td style={tableCellStyle}>{project.description}</td>
                            <td style={tableCellStyle}>{project.startDate}</td>
                            <td style={tableCellStyle}>{project.endDate}</td>
                            <td style={tableCellStyle}>{project.status}</td>
                            <td style={tableCellStyle}>{project.teamMembers.join(', ')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );


};

    // CSS styles
    const tableHeaderStyle = {
        backgroundColor: '#f2f2f2',
        border: '1px solid #dddddd',
        padding: '8px',
        textAlign: 'left',
      };
      
      const tableCellStyle = {
        border: '1px solid #dddddd',
        padding: '8px',
      };

const mapStateToProps = (state) => ({
    data: state.data.data,
    loading: state.data.loading,
    error: state.data.error,
});

const mapDispatchToProps = {
    fetchData,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProjectContainer);
