import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchData } from '../../actions/projectActions';

const ProjectContainer = ({ data, loading, error, fetchData }) => {
    

  useEffect(() => {
    // Dispatch the fetchData action when the component mounts
    //debugger;
    fetchData();
  }, [fetchData]);

  debugger;
  if (!data || data.length === 0) {
    return <p>No projects found.</p>; // Display a message when projects is null or empty
  }


  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
    
    


    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Client</th>
          <th>Description</th>
          <th>Start Date</th>
          <th>End Date</th>
          <th>Status</th>
          <th>Team Members</th>
        </tr>
      </thead>
      <tbody>
        {data.map((project) => (
          <tr key={project.id}>
            <td>{project.id}</td>
            <td>{project.name}</td>
            <td>{project.client}</td>
            <td>{project.description}</td>
            <td>{project.startDate}</td>
            <td>{project.endDate}</td>
            <td>{project.status}</td>
            <td>{project.teamMembers.join(', ')}</td>
          </tr>
        ))}
      </tbody>
    </table>












    </div>
  );
};

const mapStateToProps = (state) => ({
  data: state.data.data,
  loading: state.data.loading,
  error: state.data.error,
});

const mapDispatchToProps = {
  fetchData,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProjectContainer);
