import React, { useContext, useState } from 'react';
import axios from 'axios';
import { AuthContext } from './AuthContext';
import GlobalContext from  './GlobalContext';

const SubmitForm = () => {
   
  const [usertoken, setUsertoken] = useState('');
  const [primenumber, setPrimenumber] = useState('');
  const [result, setResult] = useState('');

  const handleUsertokenChange = (e) => {
    setUsertoken(e.target.value);
  };

  const handlePrimenumberChange = (e) => {
    setPrimenumber(e.target.value);
  };
 
  const handleSubmit = async (e) => {
    e.preventDefault();

    const primeNumberRequest = {
      //try something like <img src=x onerror="alert(\'XSS attack!\');">
      userToken: (usertoken),
      number: (primenumber),
    };


    fetch('https://localhost:44320/primenumbergate', {
      method: 'POST',
      headers: GlobalContext.headers,
      body: JSON.stringify(primeNumberRequest),
    })
      .then((response) => response.json())
      .then((data) => {
       //debugger;
        setResult(JSON.stringify(data));
        
      });

 
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        User token:
        <input type="text" value={usertoken} onChange={handleUsertokenChange} />
      </label>
      <label>
        Number:
        <input type="text" value={primenumber} onChange={handlePrimenumberChange} />
      </label>
      <button type="submit">submit</button>

      <span> {result} </span>
      <br></br>
      
    </form>
  );
};

export default SubmitForm;
