import React, { useState, useEffect } from 'react';
import GlobalContext from './Components/GlobalContext';
import EmployeeList from './Components/EmployeeCrud/EmployeeList';
import AddEmployee from './Components/EmployeeCrud/AddEmployee';

const EmployeeContainer = () => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [employees, setEmployees] = useState([]);
 // const [entities, setEntities] = useState([]);
  const handleEntityAdded = (newEntity) => {
    debugger;
    // Add the new entity to the entities array in the state
  //  setEntities([entities, newEntity]);
  setEmployees(result);
  };

  useEffect(() => {
    fetch(GlobalContext.ApiUrl, {
      method: 'GET',
      headers: GlobalContext.headers,
    })
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setEmployees(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (<div><EmployeeList employees={employees} />  <AddEmployee  onEntityAdded={handleEntityAdded}></AddEmployee></div>)

      ;
  }
};

export default EmployeeContainer;
