import React, { useState } from 'react';
import { rescheduleEmail } from './api';

const EmailReschedule = ({ email }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [rescheduleOption, setRescheduleOption] = useState(null);
  const [customDateTime, setCustomDateTime] = useState('');

  const handleReschedule = async () => {
    try {
      // Extract the emailId from the email object or wherever it's available
      const emailId = email.id;
      // Compose the message based on the selected rescheduleOption and customDateTime (if applicable)
      const message =
        rescheduleOption === 'Custom'
          ? `Custom reschedule: ${customDateTime}`
          : `Reschedule to ${rescheduleOption}`;

      // Make the AJAX request to the API with the selected date, emailId, and message
      await rescheduleEmail(emailId, message);

      // Handle success or show a notification if needed
      alert('Email re-scheduled successfully!');
    } catch (error) {
      // Handle the error or show an error notification
      alert('Failed to re-schedule email.');
    }
  };

  return (
    <div>
      {/* Render summary of the email */}
      <div onClick={() => setShowDetails(!showDetails)}>
        <span>{email.subject}</span>
        <span>{email.sender}</span>
        {/* Render other email summary details as needed */}
      </div>

      {/* Render reschedule options */}
      {showDetails && (
        <div>
          <button onClick={() => setRescheduleOption('Later today')}>Later today</button>
          <button onClick={() => setRescheduleOption('Tomorrow')}>Tomorrow</button>
          <button onClick={() => setRescheduleOption('Later this week')}>Later this week</button>
          <button onClick={() => setRescheduleOption('This weekend')}>This weekend</button>
          <button onClick={() => setRescheduleOption('Next week')}>Next week</button>
          <button onClick={() => setRescheduleOption('Custom')}>Custom date-time</button>
          {rescheduleOption === 'Custom' && (
            <input
              type="datetime-local"
              value={customDateTime}
              onChange={(e) => setCustomDateTime(e.target.value)}
            />
          )}
          <button onClick={handleReschedule}>Reschedule</button>
        </div>
      )}
    </div>
  );
};

export default EmailReschedule;
