import React, { useContext, useState } from 'react';
import axios from 'axios';
import { AuthContext } from './AuthContext';

const LoginForm = () => {
  const { setIsLoggedIn } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username.trim()) {
      setEmailError('Username/Email is required');
      return;
    } else if (!validateEmail(username.trim())) {
      setEmailError('Invalid email format');
      return;
    }

    try {
      const response = await axios.post('/api/login', { username, password });
      // Assuming the server returns a token or session upon successful login
      // You can handle the token/session and set authentication state here
      console.log('Login successful!');

      setIsLoggedIn(true);
    } catch (error) {
      console.error('Login failed:', error.response.data.message);
      // AS MENTIONED IN THE READ ME - DUE TO LACK OF TIME SERVER SIDE LOGIN WAS NOT COMPLETED 
      setIsLoggedIn(true);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Username/Email:
        <input type="text" value={username} onChange={handleUsernameChange} />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={handlePasswordChange} />
      </label>
      <button type="submit">Login</button>
      <br></br>
      {emailError && <span className='login-error'>{emailError}</span>}
    </form>
  );
};

export default LoginForm;
