import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchData } from '../../actions/projectActions';
import ProjectDetails from './ProjectDetailsPopup';

const ProjectContainer = ({ data, loading, error, fetchData }) => {

    // Initialize state to track the selected project and pop-up visibility
    const [selectedProject, setSelectedProject] = useState(null);
    const [isPopupOpen, setPopupOpen] = useState(false);

    // Event handler to open the pop-up when a project is clicked
    const handleProjectClick = (project) => {
        setSelectedProject(project);
        setPopupOpen(true);
    };

    useEffect(() => {
        fetchData();
    }, [fetchData]);


    if (!data || !data.projects || data.projects.length === 0) {
        return <p>No projects found.</p>; // Display a message when projects is null or empty
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <table style={{ borderCollapse: 'collapse', width: '100%' }}>
                <thead>
                    <tr >
                        <th >ID</th>
                        <th >Name</th>
                        <th  >Client</th>
                        <th >Description</th>
                        <th >Start Date</th>
                        <th >End Date</th>
                        <th >Status</th>
                        <th >Team Members</th>
                    </tr>
                </thead>
                <tbody>
                    {data.projects.map((project) => (
                        <tr  key={project.id} onClick={() => handleProjectClick(project)}>
                            <td >{project.id}</td>
                            <td >{project.name}</td>
                            <td >{project.client}</td>
                            <td >{project.description}</td>
                            <td >{project.startDate}</td>
                            <td >{project.endDate}</td>
                            <td >{project.status}</td>
                            <td >{project.teamMembers.join(', ')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isPopupOpen && selectedProject && (
                <div className="popup-container">
                    <ProjectDetails projectId={selectedProject.id} onClose={() => setPopupOpen(false)} />
                </div>
            )}


        </div>
    );


};


const mapStateToProps = (state) => ({
    data: state.data.data,
    loading: state.data.loading,
    error: state.data.error,
});

const mapDispatchToProps = {
    fetchData,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProjectContainer);
